package ControlStatements;

public class IfElseIf {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int findnumber=15;
		
		if(findnumber==1)
		{
			System.out.print("number is "+findnumber);
		}
		else if(findnumber==20)
		{
			System.out.print("number is "+findnumber);
			
		}
		else if(findnumber==10)
		{
			System.out.print("number is "+findnumber);
			
		}
		else
		{
			System.out.print("number is Not Found"+findnumber);
			
		}

	}

}
