package StringDemo;

public class StringTestingDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//String literal
		
		String name="Kiran Kumar";
		
		
		
		System.out.println(name);
		
		//String using new keyword
		
		String obj=new String("Kiran And Nabi");
	
		
	String	data=obj.concat("KK");
		
		System.out.println(data);
	}

}
